'use strict';

// MODULES //

var negating = require( './cneg.js');

// MAIN //

/**
* Computes the negative of a complex number.
*
* @param {number} re - real component
* @param {number} im - imaginary component
* @returns {Array} output array
*
* @example
* var v = cneg( 2.0, 4.0 );
* // returns [ -2.0, -4.0 ]
*/

function cneg( out, re, im ) {
  if ( arguments.length === 2 ) {
    return negating( new Array( 2 ), out, re );
  }
  return negating( out, re, im );
} // end FUNCTION cneg()

// EXPORTS //

module.exports = cneg;
