'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var Float64Array = require( '@stdlib/types/array/float64' ); // eslint-disable-line no-redeclare
var cneg = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof cneg, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns the negative of a complex numbers', function test( t ) {
	var expected;
	var v;

	v = cneg( 5.0, 3.0 );
	expected = [ -5.0, -3.0 ];

	t.deepEqual( v, expected, 'returns expected value' );

	t.end();
});

tape( 'the function returns the negative of a complex numbers (output array)', function test( t ) {
	var expected;
	var out;
	var v;

	out = new Array( 2 );
	v = cneg( out, 5.0, 3.0 );

	expected = [ -5.0, -3.0 ];

	t.deepEqual( v, expected, 'returns expected value' );
	t.strictEqual( v, out, 'returns output value' );

	t.end();
});

tape( 'the function returns the negative of a complex numbers (output typed array)', function test( t ) {
	var expected;
	var out;
	var v;

	out = new Float64Array( 2 );
	v = cneg( out, 5.0, 3.0 );

	expected = new Float64Array( [ -5.0, -3.0 ] );

	t.deepEqual( v, expected, 'returns expected value' );
	t.strictEqual( v, out, 'returns output value' );

	t.end();
});

tape( 'the function returns the negative of a complex numbers (output object)', function test( t ) {
	var expected;
	var out;
	var v;

	out = {};
	v = cneg( out, 5.0, 3.0 );

	expected = {
		'0': -5.0,
		'1': -3.0
	};
	t.deepEqual( v, expected, 'returns expected value' );
	t.strictEqual( v, out, 'returns output value' );

	t.end();
});

tape( 'if a real or imaginary component is `NaN`, the component is `NaN`', function test( t ) {
	var v;

	v = cneg( NaN, 3.0 );
	t.strictEqual( isnan( v[ 0 ] ), true, 'returns NaN' );

	v = cneg( 5.0, NaN );
	t.strictEqual( isnan( v[ 1 ] ), true, 'returns NaN' );

	v = cneg( NaN, NaN );
	t.strictEqual( isnan( v[ 0 ] ), true, 'returns NaN' );
	t.strictEqual( isnan( v[ 1 ] ), true, 'returns NaN' );

	t.end();
});
